<script>
import {reactive, onMounted} from 'vue'
export default {
	name: "clockAppIcon",
	setup() {
		let deg = reactive({})
		function getTime() {
			let d = new Date()
			let hours = d.getHours()
			let minutes = d.getMinutes()
			let seconds = d.getSeconds()
			deg.h = 30 * hours + .5 * minutes
			deg.m = 6 * minutes + .1 * seconds
			deg.s = 6 * seconds
		}

		onMounted(() => {
			getTime()
			setInterval(getTime, 1000)
		})
		return { deg }
	}
}
</script>

